mapper.update:
UPDATE FI_REGISTER_INFO


        VERIFICATION_CODE = #{verificationCode, jdbcType=VARCHAR},


        EFFECT_START_TIME =  #{effectStartTime, jdbcType=NUMERIC},


        EFFECT_END_TIME = #{effectEndTime, jdbcType=NUMERIC}



    EMAIL = #{email, jdbcType=VARCHAR}

mapper.select:
SELECT COUNT(*) FROM FI_REGISTER_INFO


        AND EMAIL = #{email, jdbcType=VARCHAR}


        AND VERIFICATION_CODE = #{verificationCode, jdbcType=VARCHAR}


        AND EFFECT_START_TIME <= #{currTime, jdbcType=NUMERIC}
        AND EFFECT_END_TIME >= #{currTime, jdbcType=NUMERIC}
